sources.scss/_offers.scss:
.offerContainer {
  padding: 5rem 0;
  // max-width: 100rem;
  margin: auto;
  position: relative;

  .swoosh {
    display: none;
    position: absolute;
    bottom: 0;
    left: -10rem;
    width: 70rem;
    opacity: 0.5;
    z-index: -10;

    @include respond(desktop-xl) {
      display: block;
    }
  }

  .offerFlex {
    display: flex;
    flex-flow: column nowrap;
    justify-content: center;
    align-items: center;
    // border: 1px solid red;
    max-width: 110rem;

    @include respond(landscape) {
      flex-flow: row wrap;
      justify-content: space-evenly;
      width: 95%;
      margin: auto;
      gap: 2rem;
    }

    @include respond(desktop-lg) {
      margin: 2rem auto 0 auto;
    }

    .offer {
      margin-top: 5rem;
      width: 85%;

      @include respond(landscape) {
        width: 40rem;
      }

      .offer-img {
        position: relative;

        .component-img {
          width: 100%;
          height: 30vw;
          object-fit: cover;
          border-radius: $border-radius-small;
          box-shadow: 0 10px 50px rgba($color: #000, $alpha: 0.5);

          @include respond(landscape) {
            height: 20rem;
          }
        }

        .sprinkle-top-left {
          position: absolute;
          width: clamp(40px, 10vw, 5rem);
          transform: translate(-70%, -70%);
        }

        .sprinkle-bottom-right {
          position: absolute;
          bottom: 0;
          right: 0;
          width: clamp(40px, 10vw, 5rem);
          transform: translate(40%, 70%);
        }
      }

      .offer-text {
        font-weight: 100;
        line-height: 1.8;
        font-size: 1.3rem;
        // box-shadow: 0 5px 10px 5px rgba($color: #000, $alpha: 1);

        &--headingContainer {
          display: flex;
          align-items: flex-end;
          justify-content: center;
          padding: 1rem;

          .offer-icon {
            width: 25px;
            margin-right: 0.5rem;
            margin-bottom: 0.5rem;
          }

          h3 {
            font-size: 1.8rem;
            font-weight: 500;
          }
        }

        p {
          color: #9f9f9f;
        }
      }
    }
  }
}

